def extract_key_insights(context_content):
    """Extract only the most relevant insights from context"""
    lines = context_content.split('\n')

    # Extract current intent
    intent = ""
    for line in lines:
        if line.startswith("Citizen is"):
            intent = line.strip()
            break

    # Extract collaborators mentioned
    collaborators = []
    for line in lines:
        if "🤝" in line and len(line.strip()) > 5:
            collab = line.replace("🤝", "").strip()
            if collab and len(collab) < 50:
                collaborators.append(collab)

    # Build concise insight
    if intent:
        insight = f"Context: {intent}"
        if collaborators:
            insight += f" | With: {', '.join(collaborators[:2])}"
        return insight

    return None

