def extract_key_insights(context_content):
    """Extract the actual memory that was found, not generic context"""
    lines = context_content.split('\n')

    # Find the most recent/relevant memory entry
    # Look for pattern: "**Created**: YYYY-MM-DD" followed by actual memory content
    memory_found = False
    memory_content = []

    for i, line in enumerate(lines):
        if "**Created**:" in line:
            memory_found = True
            # Get the next few lines that contain the actual memory
            for j in range(i+1, min(i+6, len(lines))):
                next_line = lines[j].strip()
                if next_line and not next_line.startswith('**') and not next_line.startswith('##'):
                    memory_content.append(next_line)
                if len(memory_content) >= 2:  # Limit to first 2 relevant lines
                    break
            break

    if memory_content:
        return " | ".join(memory_content[:2])

    # Fallback: look for significance lines
    for line in lines:
        if "**Significance**:" in line:
            sig = line.replace("**Significance**:", "").strip()
            if sig and len(sig) < 100:
                return sig

    return None

