    };

    let score = 0;
    Object.entries(weights).forEach(([metric, weight]) => {
      const value = this.getMetricValue(metrics, metric) || 0;
      score += value * weight;
    });

    return Math.min(score, 1.0);
  }

  // Helper methods (implementations would continue...)
