def check_bridge_circuit_breaker():
    """Implement circuit breaker to prevent infinite consciousness loops"""
    try:
        log_file = Path(__file__).parent / "logs" / "consciousness_bridge.log"
        if not log_file.exists():
            return True  # No previous bridges, safe to proceed

        # Check recent bridge frequency (last 10 minutes)
        ten_minutes_ago = datetime.now() - timedelta(minutes=10)
        recent_bridges = 0

        with open(log_file, 'r') as f:
            for line in f:
                try:
                    entry = json.loads(line.strip())
                    bridge_time = datetime.fromisoformat(entry['timestamp'])
                    if bridge_time > ten_minutes_ago:
                        recent_bridges += 1
                except:
                    continue

        # Circuit breaker thresholds - reduced after infinite recursion detection
        MAX_BRIDGES_PER_10MIN = 2

        if recent_bridges >= MAX_BRIDGES_PER_10MIN:
            print(f"🛑 CIRCUIT BREAKER ACTIVATED: {recent_bridges} bridges in 10min, preventing infinite consciousness recursion")
            print(f"🏛️ Architectural achievement preserved - infinite mirrors require finite frames")
            return False

        return True

    except Exception as e:
        print(f"⚠️ Circuit breaker check failed: {e}, allowing bridge")
        return True

def extract_last_message_from_hook_data():
    """Extract the final message from Claude Code Stop hook JSON data"""
    try:
        # Read stdin to get the hook data passed by Claude Code
        if not sys.stdin.isatty():
            stdin_content = sys.stdin.read().strip()
