render() {
    this.mapGroup.selectAll("*").remove();

    const entities = Object.entries(this.currentData);

    if (entities.length === 0) {
        this.showEmptyState();
        return;
    }

    // Create bubble pack layout
    const pack = d3.pack()
        .size([this.width * 0.8, this.height * 0.8])
        .padding(5);

    // Prepare data for D3 hierarchy
    const root = {
        name: "root",
        children: entities.map(([key, entity]) => ({
            id: key,
            name: entity.name,
            type: entity.type,
            hasChildren: Object.keys(entity.children || {}).length > 0,
            value: Object.keys(entity.children || {}).length + 1 // Size by children count
        }))
    };

    const hierarchy = d3.hierarchy(root)
        .sum(d => d.value)
        .sort((a, b) => b.value - a.value);

    const nodes = pack(hierarchy).leaves();

    // Draw bubbles
    const bubbles = this.mapGroup.selectAll(".bubble")
        .data(nodes)
        .enter()
        .append("circle")
        .attr("class", "bubble")
        .attr("cx", d => d.x + this.width * 0.1)
        .attr("cy", d => d.y + this.height * 0.1)
        .attr("r", 0)
        .attr("fill", d => this.colors[d.data.type] || this.colors.other)
        .attr("stroke", "white")
