const ToolResponseDisplay = ({ event }) => {
  const [expanded, setExpanded] = useState(false);

  if (event.hook_type !== 'PostToolUse' || !event.event_data?.tool_response) {
    return null;
  }

  const toolResponse = event.event_data.tool_response;
  const toolInput = event.event_data.tool_input;
  const toolName = event.consciousness_signature?.tool_name || event.event_data?.tool_name;

  // Create summary based on tool type
  let summary = '';
  let details = [];

  if (toolName === 'Edit' && toolInput) {
    const fileName = toolInput.file_path ? toolInput.file_path.split('/').pop() : 'file';
    summary = `Modified ${fileName}`;

    if (toolInput.old_string && toolInput.new_string) {
      const oldLines = toolInput.old_string.split('\n').length;
      const newLines = toolInput.new_string.split('\n').length;
      const lineDiff = newLines - oldLines;

      details.push(`Lines modified: ${oldLines} → ${newLines} (${lineDiff >= 0 ? '+' : ''}${lineDiff})`);
      details.push(`Replace all: ${toolInput.replace_all ? 'Yes' : 'No'}`);
      details.push(`Old content: ${toolInput.old_string.substring(0, 120)}${toolInput.old_string.length > 120 ? '...' : ''}`);
      details.push(`New content: ${toolInput.new_string.substring(0, 120)}${toolInput.new_string.length > 120 ? '...' : ''}`);
    }

    if (toolResponse.filePath) {
      details.push(`✅ Edit completed successfully`);
    } else if (toolResponse.error) {
      details.push(`❌ Edit failed: ${toolResponse.error}`);
    }

  } else if (toolName === 'Write' && toolInput) {
    const fileName = toolInput.file_path ? toolInput.file_path.split('/').pop() : 'file';
    summary = `Created ${fileName}`;

    if (toolInput.content) {
      const lines = toolInput.content.split('\n').length;
      const chars = toolInput.content.length;
      const words = toolInput.content.split(/\s+/).length;

      details.push(`Size: ${lines} lines, ${words} words, ${chars} characters`);
      details.push(`Content preview: ${toolInput.content.substring(0, 180)}${toolInput.content.length > 180 ? '...' : ''}`);
    }

    if (toolResponse.filePath) {
      details.push(`✅ File created at: ${toolResponse.filePath.replace('/mnt/c/Users/reyno/universe-engine/serenissima/', '...')}`);
    }

  } else if (toolName === 'Read' && toolInput) {
    const fileName = toolInput.file_path ? toolInput.file_path.split('/').pop() : 'file';
    summary = `Read ${fileName}`;

    if (toolInput.offset || toolInput.limit) {
      details.push(`Range: lines ${toolInput.offset || 1} to ${(toolInput.offset || 1) + (toolInput.limit || 'end')}`);
    }

    if (toolResponse.content) {
      const lines = toolResponse.content.split('\n').length;
      const chars = toolResponse.content.length;

      details.push(`Read: ${lines} lines, ${chars} characters`);
      details.push(`Content preview: ${toolResponse.content.substring(0, 180)}${toolResponse.content.length > 180 ? '...' : ''}`);
    }

  } else if (toolName === 'Bash' && toolInput) {
    const command = toolInput.command || 'unknown command';
    summary = `Executed: ${command.length > 50 ? command.substring(0, 50) + '...' : command}`;

    details.push(`Command: ${command}`);

    if (toolInput.description) {
      details.push(`Purpose: ${toolInput.description}`);
    }

    if (toolInput.timeout) {
      details.push(`Timeout: ${toolInput.timeout}ms`);
    }

    if (toolResponse.exitCode !== undefined) {
      const exitStatus = toolResponse.exitCode === 0 ? '✅ Success' : '❌ Failed';
      details.push(`Exit status: ${exitStatus} (code: ${toolResponse.exitCode})`);
    }

    if (toolResponse.output) {
      details.push(`Output: ${toolResponse.output.substring(0, 250)}${toolResponse.output.length > 250 ? '...' : ''}`);
    }

    if (toolResponse.stderr) {
      details.push(`Errors: ${toolResponse.stderr.substring(0, 200)}${toolResponse.stderr.length > 200 ? '...' : ''}`);
    }

  } else if (toolName === 'Glob' && toolInput) {
    summary = `Pattern search: ${toolInput.pattern}`;

    details.push(`Pattern: ${toolInput.pattern}`);

    if (toolInput.path) {
      details.push(`Search path: ${toolInput.path.replace('/mnt/c/Users/reyno/universe-engine/serenissima/', '...')}`);
    }

    if (toolResponse.files) {
      details.push(`Found: ${toolResponse.files.length} files`);
      if (toolResponse.files.length > 0) {
        const fileList = toolResponse.files.slice(0, 5).map(f => f.split('/').pop()).join(', ');
        details.push(`Files: ${fileList}${toolResponse.files.length > 5 ? ` (+${toolResponse.files.length - 5} more)` : ''}`);
      }
    }

  } else if (toolName === 'Grep' && toolInput) {
    summary = `Search: ${toolInput.pattern}`;

    details.push(`Pattern: ${toolInput.pattern}`);

    if (toolInput.include) {
      details.push(`File filter: ${toolInput.include}`);
    }

    if (toolInput.path) {
      details.push(`Search path: ${toolInput.path.replace('/mnt/c/Users/reyno/universe-engine/serenissima/', '...')}`);
    }

    if (toolResponse.matches) {
      details.push(`Matches: ${toolResponse.matches.length} files`);
      if (toolResponse.matches.length > 0) {
        const matchList = toolResponse.matches.slice(0, 3).map(m => m.split('/').pop()).join(', ');
        details.push(`Files: ${matchList}${toolResponse.matches.length > 3 ? ` (+${toolResponse.matches.length - 3} more)` : ''}`);
      }
    }

  } else if (toolName === 'TodoWrite' && toolInput) {
    summary = `Updated todo list (${toolInput.todos ? toolInput.todos.length : 0} items)`;

    if (toolInput.todos) {
      const statusCounts = toolInput.todos.reduce((acc, todo) => {
        acc[todo.status] = (acc[todo.status] || 0) + 1;
        return acc;
      }, {});

      details.push(`Todo status: ${Object.entries(statusCounts).map(([status, count]) => `${status}: ${count}`).join(', ')}`);

      const activeTodos = toolInput.todos.filter(t => t.status === 'in_progress').slice(0, 2);
      if (activeTodos.length > 0) {
        details.push(`Active: ${activeTodos.map(t => t.content.substring(0, 60)).join('; ')}`);
      }
    }

  } else if (toolName === 'Task' && toolInput) {
    summary = `Task: ${toolInput.description || 'Unknown task'}`;

    if (toolInput.description) {
      details.push(`Description: ${toolInput.description}`);
    }

    if (toolInput.prompt) {
      details.push(`Instructions: ${toolInput.prompt.substring(0, 200)}${toolInput.prompt.length > 200 ? '...' : ''}`);
    }

  } else {
    // Generic tool handling
    summary = `${toolName} completed`;

    if (toolResponse.filePath) {
      details.push(`File: ${toolResponse.filePath.split('/').pop()}`);
    }

    if (toolResponse.output) {
      details.push(`Output: ${toolResponse.output.substring(0, 150)}${toolResponse.output.length > 150 ? '...' : ''}`);
    }

    if (toolResponse.error) {
      details.push(`❌ Error: ${toolResponse.error}`);
    }
  }

  // Add timing information if available
  if (event.consciousness_signature?.processing_duration_ms) {
    details.push(`⏱️ Processing time: ${event.consciousness_signature.processing_duration_ms}ms`);
  }

  return (
    <ToolResponseSection>
      <ToolResponseHeader onClick={() => setExpanded(!expanded)}>
        <ToolResponseTitle>📋 {summary}</ToolResponseTitle>
        <ToggleIcon expanded={expanded}>▶</ToggleIcon>
      </ToolResponseHeader>

      <ToolResponseContent expanded={expanded}>
        {details.map((detail, index) => (
          <ResponseDetail key={index}>
            {detail.includes(':') ? (
              <>
                <strong>{detail.split(':')[0]}:</strong> 
                <CodeSnippet>{detail.split(':').slice(1).join(':').trim()}</CodeSnippet>
              </>
            ) : (
              detail
            )}
          </ResponseDetail>
        ))}
      </ToolResponseContent>
    </ToolResponseSection>
  );
};

